require_once(dirname(__FILE__).'/../../xf/wp/AAdminMenu.php');

/**
 * wpew_admin_wpew_Page
 *
 * @package wpew
 * @subpackage admin
 */
class wpew_admin_wpew_Page extends xf_wp_AAdminMenu {
	
	/**
	 * @var string $title This page's title
	 */
	public $title = "Extensible Widgets";
	/**
	 * @var string $title Optional menu title, defaults to the page's title
	 */
	public $menuTitle = "Ext. Widgets";
	
	/**
	 * Used internally for a common content header
	 *
	 * @return void
	 */
	public function header( $title = '' ) { 
		if( empty($title) ) $title = $this->currentPage->title; ?>
		<div class="wrap">
			<div id="icon-themes" class="icon32"><br /></div>
			<h2><?php echo $this->title; ?> &raquo; <?php echo $title; ?></h2>
			<p><?php 
			$navs = array();
			reset($this->_children);
			do {
				$child =& current($this->_children);
				$class = ($child == $this->currentPage) ? 'button-primary' : 'button';
				$navs[] = '<a href="'.$child->pageURI.'" class="'.$class.'">'.$child->title.'</a>';
			} while( next($this->_children) !== false ); 
			echo implode(' | ', $navs ); ?></p>
			<?php do_action( 'admin_notices' ); ?>
	<?php }
	
	/**
	 * Used internally for a common content header
	 *
	 * @return void
	 */
	public function footer() { ?>
		<br />
		<form action="https://www.paypal.com/cgi-bin/webscr" method="post">
			<input type="hidden" name="cmd" value="_s-xclick">
			<input type="hidden" name="encrypted" value="-----BEGIN PKCS7----------END PKCS7-----">
			<p class="description">Thank you for using <strong>Extensible Widgets Version <?php echo $this->root->version; ?></strong> by <a href="http://jimisaacs.com" target="wpew_window">Jim Isaacs</a> | <a href="http://wordpress.org/extend/plugins/extensible-widgets/" target="wpew_window">Documentation</a> | <a href="http://jidd.jimisaacs.com/archives/863#footer" target="wpew_window">Feedback</a> | Please <input style="vertical-align:middle;" type="image" src="http://jidd.jimisaacs.com/wp-content/uploads/2010/02/donate_button.png" border="0" name="submit" alt="Help Me! Heeeelp Meeee!"> if it has been of any use.<img alt="" border="0" src="https://www.paypal.com/en_US/i/scr/pixel.gif" width="1" height="1"></p>
		</form>
		</div>
	<?php }
	
	// STATES
	
	/**
	 * @see xf_wp_IPluggable::index()
	 */
	public function index() {}
}
